package com.stringprog;

public class EqualMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String abc="Lets play jumanji";
		char a=abc.charAt(0);
		char b=abc.charAt(3);
		
		System.out.println(a+b);
		System.out.println(""+a+b);
		
		System.out.println(abc.substring(5));
	}

}
